const arr = (items[0].json.titles || []).slice(0, 10);
return arr.map(t => {
  const m = String(t).match(/^(.*?)(?:\s*\((\d{4})\))?$/);
  const title = (m?.[1] || '').trim();
  const year  = m?.[2] ? Number(m[2]) : null;
  const lowerTitle = title.toLowerCase();
  return {
    json: {
      lowerTitle,
      likePrefix: lowerTitle + '%',
      likeContains: '%' + lowerTitle + '%',
      q_year: Number.isFinite(year) ? year : null,
    }
  };
});

function mdEscape(s) {
  return String(s ?? '')
    .replace(/\|/g, '\\|')  // Pipe escapen
    .replace(/`/g, '\\`')   // Backtick escapen
    .trim();
}

function toList(x) {
  if (Array.isArray(x)) return x.filter(Boolean);
  if (x == null) return [];
  return String(x).split(',').map(s => s.trim()).filter(Boolean);
}

function fmtDe(n) {
  if (n === null || n === undefined || n === '') return 'â€“';
  const num = Number(n);
  if (!Number.isFinite(num)) return 'â€“';
  return num.toFixed(1).replace('.', ',');
}

function bar5(score01to10) {
  if (score01to10 == null || score01to10 === '') return '`â˜†â˜†â˜†â˜†â˜†` â€“';
  const s5 = Math.max(0, Math.min(5, Math.round(Number(score01to10) / 2)));
  const bar = 'â˜…'.repeat(s5) + 'â˜†'.repeat(5 - s5);
  const num = (Number(score01to10) / 2);
  const disp = num.toFixed(1).replace('.', ','); // DE-Komma
  return '`' + bar + '` ' + disp;
}

function truncate(s, max=30) {
  s = String(s ?? '');
  return s.length <= max ? s : s.slice(0, max - 1) + 'â€¦';
}

const rows = items.map(i => i.json).filter(r => r && r.title);

let md = `### ðŸŽ¬ Filmvergleich\n\n`;

if (!rows.length) {
  return [{ json: { markdown: md + '*Keine Treffer.*' } }];
}

// Kopf
md += '| Titel | â­ Kritiker | ðŸ‘¥ Publikum | Genres | Regie | Jahr | â± |\n';
md += '|---|:---:|:---:|:---:|---|---|:---:|\n';

// Zeilen
for (const r of rows) {
  const title = '**' + mdEscape(r.title) + '**';
  const year = r.year ?? 'â€“';
  const runtime = r.duration_min ?? 'â€“';
  const critics = bar5(r.rating);
  const audience = bar5(r.audience_rating);

  const genresFull = toList(r.genres).join(', ');
  const directorsFull = toList(r.directors).join(', ');

  const genres = truncate(genresFull, 22);
  const directors = truncate(directorsFull, 22);

  const release = r.release_date || 'â€“';

  md += `| ${title} | ${critics} | ${audience} | ${mdEscape(genres)} | ${mdEscape(directors)} | ${year} | ${runtime} |\n`;
}

// Stats/Fazit
const stats = rows.map(r => ({
  title: r.title,
  runtime: r.duration_min != null ? Number(r.duration_min) : null,
  critics: r.rating != null ? Number(r.rating) : null,
  audience: r.audience_rating != null ? Number(r.audience_rating) : null,
  genres: toList(r.genres).map(g => g.toLowerCase()),
}));

const bestCritics = stats.filter(x => Number.isFinite(x.critics))
  .sort((a,b) => b.critics - a.critics || (Number.isFinite(a.audience)&&Number.isFinite(b.audience) ? b.audience - a.audience : 0) || 0)[0];

const longest = stats.filter(x => Number.isFinite(x.runtime))
  .sort((a,b) => b.runtime - a.runtime)[0];

let overlap = [];
if (stats.length >= 2) {
  const base = new Set(stats[0].genres);
  const common = stats.slice(1).reduce((acc, cur) => {
    const s = new Set(cur.genres);
    return new Set([...acc].filter(x => s.has(x)));
  }, base);
  overlap = [...common];
}

md += `\n**Kurzfazit**\n`;
if (bestCritics) md += `- ðŸ† Bestes Rating: *${mdEscape(bestCritics.title)}* (${fmtDe(bestCritics.critics/2)})\n`;
if (longest) md += `- â± LÃ¤ngste Laufzeit: *${mdEscape(longest.title)}* (${longest.runtime})\n`;
if (overlap.length) md += `- ðŸ§¬ Gemeinsame Genres: ${overlap.map(g => '`'+g+'`').join(', ')}\n`;

return [{ json: { markdown: md } }];
