parameters.jsCode:
const feeds = [
  "https://dierkehouben.com/feed/?post_type=post",
  "https://www.mindsetmovers.de/feed/",
  "https://t3n.de/rss.xml",
  "https://business-elf.de/feed/",
  "https://www.epicwork.de/feed/",
  "https://b-h.ch/feed/",
  "https://www.targetter.de/feed/",
];

return feeds.map(url => {
  return {
    json: {
      url
    }
  };
});

const input = $input.item.json.data;

// Jeden Eintrag in einen String umwandeln
const entries = input.map(entry => {
  const title = entry.title || "";
  const snippet = entry.contentSnippet || "";
  return `${title}\n${snippet}`;
});

// Alle mit --- trennen
const combined = entries.join("\n---\n");

return [
  {
    json: {
      text: combined
    }
  }
];

