const input = $input.item.json.data;

// Jeden Eintrag in einen String umwandeln
const entries = input.map(entry => {
  const title = entry.title || "";
  const snippet = entry.contentSnippet || "";
  return `${title}\n${snippet}`;
});

// Alle mit --- trennen
const combined = entries.join("\n---\n");

return [
  {
    json: {
      text: combined
    }
  }
];

