// Extract and merge JSON from Code Node output
const jsonData = $input.all().map(item => item.json);

// Function to format structured text for Google Docs
function formatContent(data) {
    let content = `# Research\n\n`;

    data.forEach(section => {
        content += `## ${section.section}\n`;

        // Loop through all keys except 'section'
        Object.keys(section).forEach(key => {
            if (key !== "section") {
                if (Array.isArray(section[key])) {
                    content += `- **${key.replace(/_/g, " ")}**:\n  - ` + section[key].join("\n  - ") + `\n\n`;
                } else {
                    content += `- **${key.replace(/_/g, " ")}**: ${section[key]}\n\n`;
                }
            }
        });
    });

    return content.trim();
}

// Generate the formatted text output
const documentText = formatContent(jsonData);

// Return structured text for Google Docs
return [{
    json: {
        document_name: "Research",
        content: documentText // This ensures 'content' is always returned
    }
}];

const MAX_LENGTH = 2000;
const pages = [];
const items = $input.all()


for (const item of items) {
  const rawOutput = item.json.output;

  // Titel extrahieren
  const titleMatch = rawOutput.match(/^#\s+(.*)/);
  const title = titleMatch ? titleMatch[1] : 'Untitled';

  // Inhalt bereinigen (alles nach Titelzeile)
  let content = rawOutput.split('\n').slice(1).join('\n').trim();

  // Escape-Korrektur
  content = content
    .replace(/\\"/g, '"')
    .replace(/\\n/g, '\n')
    .replace(/\r\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n'); // unnötige Leerzeilen reduzieren

  const children = [];

  if (content.length <= MAX_LENGTH) {
    // Ein einzelner Block
    children.push({
      object: 'block',
      type: 'paragraph',
      paragraph: {
        rich_text: [{
          type: 'text',
          text: { content }
        }]
      }
    });
  } else {
    // Splitten in max. 2000 Zeichen pro Block
    let remaining = content;

    while (remaining.length > 0) {
      let chunk = remaining.slice(0, MAX_LENGTH);

      // Optional: möglichst sauber an Absatzgrenze schneiden
      const lastBreak = chunk.lastIndexOf('\n\n');
      if (lastBreak > 0 && remaining.length > MAX_LENGTH) {
        chunk = remaining.slice(0, lastBreak);
      }

      children.push({
        object: 'block',
        type: 'paragraph',
        paragraph: {
          rich_text: [{
            type: 'text',
            text: { content: chunk.trim() }
          }]
        }
      });

      remaining = remaining.slice(chunk.length).trim();
    }
  }

  pages.push({
    json: {
      properties: {
        Name: [
          {
            text: {
              content: title
            }
          }
        ]
      },
      children
    }
  });
}

return pages;
